import React, { Component } from 'react';
import { withRouter, Redirect } from "react-router-dom";

class ListForm extends Component {

    constructor(props) {
        super(props);
        this.state = {
            value: {
                title: "",
                description: ""
            },
            todoitem: []
        };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    }


    componentDidMount() {
        fetch(`/item/${this.id["id"]}`)
        .then(res => res.json())
        .then(todoitem => this.setState({todoitem}, () => console.log(todoitem)));
    }

    handleChange(event) {
        event.preventDefault();
        this.setState({[event.taget.list]: event.target.value})
    }
    handleSubmit(event) {
        event.preventDefault();
        fetch(`/item/update/${this.state.todoitem[0].id}/${this.state.todoitem[0].category}/${this.state.value}`, {method: 'PUT'})
        .then(res => console.log(res));
        const {history} = this.props;
        history.push(`/todolists/${this.id["id"]}`);
    }

    render() {
        return (
            <div>
                <h1>Update Item</h1>

            <form onSubmit={(event) => this.handleSubmit(event)}>>
      <label>
        title
        <input
          type="text"
          name="title"
          value={this.statevalue.title}
          onChange={this.handleChange}
        />
      </label>
      <label>
        description
        <input
          type="text"
          name="description"
          value={this.state.value.description}
          onChange={this.handleChange}
        />
      </label>
    </form>
            </div>
        )
    }
}

export default withRouter(ListForm);
