import React, { Component } from 'react';
import { withRouter, Link, useParams } from "react-router-dom";

class Todoitems extends Component {

    constructor(props) {
        super(props);
        this.state = {
            todoitems: []
        }
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    componentDidMount() {
        let {id} = this.props.match.params;
        fetch(`/todolists/${id}`)
        .then(res => res.json())
        .then(todoitems => this.setState({todoitems}, () => console.log(todoitems)));
    }

    handleSubmit(event) {
        event.preventDefault();
        fetch(`/item/update/${this.state.todoitem[0].id}/${this.state.todoitem[0].category}/${this.state.value}`, {method: 'PUT'})
        .then(res => console.log(res));
    }

    render() {
        return (
            <div>
                <h1>TodoItems</h1>
            <ul>
                {this.state.todoitems.map(todoitem =>
                 <Link to={`/todolists/item/edit/${todoitem.id}`}>
                <li>{todoitem.title}</li>
                <form onSubmit={(event) => this.handleSubmit(event)}>
                <label>
                    title:
                    <textarea value={this.state.todoitem.title} onChange={this.handleChange} />
                    </label>
                    <input type="submit" title="Submit" />
            </form>
                 </Link>

                    )}
            </ul>
            </div>
        )
    }
}

export default withRouter(Todoitems);
