import React, { Component } from 'react';
import { withRouter,Link, useParams } from "react-router-dom";

class ItemForm extends Component {

    constructor(props) {
        super(props);
        this.state = {
            value: null,
        };


    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    }

    componentDidMount() {
        let {id} = this.props.match.params;
        fetch(`/item/${id}`)
        .then(res => res.json())
        .then(todoitem => this.setState({todoitem}, () => console.log(todoitem)));
    }

    handleChange(event) {
        this.setState({value: event.target.value});
        console.log(this.state.value);
    }
    handleSubmit(event) {
        const requestOptions = {
            method: 'PUT',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ title: this.state.value })
        };
        fetch(`/item/update/${this.state.todoitem.id}/${this.state.todoitem.category}`, requestOptions)
            .then(response => response.json())
    }

    render() {
        return (
            <div>
                <h1>Update Item</h1>
            <form onSubmit={this.handleSubmit}>
                <label>
                    title:
                    <textarea value={this.state.todoitem.title} onChange={this.handleChange} />
                    </label>
                    <input type="submit" title="Submit" />
            </form>
            </div>
        )
    }
}

export default withRouter(ItemForm);
